function formValidator(){nameField.addEventListener("keyup",function(a){nameField.validity.typeMismatch?(a.preventDefault(),nameField.setCustomValidity("Bitte geben Sie nur Buchstaben ein - z.B. Max Mustermann")):(a.preventDefault(),nameField.setCustomValidity(""))}),emailField.addEventListener("keyup",function(a){emailField.validity.typeMismatch?(a.preventDefault(),emailField.setCustomValidity("Bitte geben Sie eine richtige E-Mail-Adresse - z.B. abc@def.de")):(a.preventDefault(),email.setCustomValidity(""))}),phoneField.addEventListener("keyup",function(a){phoneField.validity.typeMismatch?(a.preventDefault(),phoneField.setCustomValidity("Bitte geben Sie nur Zahlen ein - z.B. 030123456")):(a.preventDefault(),phoneField.setCustomValidity(""))}),commentaryField.addEventListener("keyup",function(a){commentaryField.validity.typeMismatch?(a.preventDefault(),commentaryField.setCustomValidity("Sie k&ouml;nnen maximal 500 Zeichen eingeben.")):(a.preventDefault(),commentaryField.setCustomValidity(""))})}var nameField=document.getElementById("name"),emailField=document.getElementById("email"),phoneField=document.getElementById("telephone"),commentaryField=document.getElementById("commentary");window.onload=function(){formValidator()};